function hero({name, imagem}) {
    return (
        <div>
            <h2>{name}</h2>
            <img src={imagem} alt="hero"/>
        </div>
    )
}

function enemy({name, imagem}) {
    return (
        <div>
            <h2>{name}</h2>
            <img src={imagem} alt="enemy"/>
        </div>
    )
}

function arena() {
    return (
        <div>
            <hero name = "batman" imagem = {"/home/marcelo/Downloads/imgs03/batman.png"}/>
            <enemy name = "coringa" imagem = {"/home/marcelo/Downloads/imgs03/coringa.png"}/>
        </div>
    )
}

function world({Children}) {
    return(
        <div>
            {Children}
        </div>
    )
}

