const Filho = ({altura, peso}) => {
    let imc = peso/(altura*altura) 

    function mensagens() {
        if (imc < 18) {return "Abaixo do peso"}
        else if (imc > 25) {return "Acima do peso"}
        else {return "Peso ideal"}
    }

    return (
        <div>
            <h2>
                imc: {imc }
                {mensagens()}
            </h2>
        </div>
    )
}

export {Filho}
