const Estado = () => {

    let contador = 0

    const incrementar = () => {
        contador++
    }

    return (
        <div>
            <h2>Contador: </h2>
            <button
            onClick={() => incrementar()}
            >
                incrementar contador :P
            </button>
        </div>
    )
}

export {Estado}
